# Find the longest sub-string that when reversed matches itself
# The string to search
string = ""
len = string.length
print "The length of the string is #{len}\n"
match = ""
(0..len).each do |i|
	(i..len).each do |j|
		# Check the sub-string against the reverse of the sub-string
		if string[i..j] == string[i..j].reverse then
			# We have a match, is it longer than the last match?
			if string[i..j].length > match.length then
				# Yes, we have a new match.
				match = string[i..j]
				print match + "\n"
			end
		end
	end
end
print "Longest match: '#{match}'\n"